import swal from 'sweetalert';




export const guardarPorcentaje = (e) => {
    var input = document.getElementById('porcentajeC1');//inicio 1
    input.addEventListener('input', function () {
        if (this.value.length > 2)
            this.value = this.value.slice(0, 2);
    })

    if (input.value <= 100) {
        if (input.value.length === 2) {
            //ejecutar metodo de guardar
            swal("describa el nombre del creterio:", {
                content: "input",
              })
              .then((value) => {
                swal(`You typed: ${value}`);
              });
            console.log("ejecutar metodo de guardar 1")
        }
    } else {
        alert("no puede exeder de 100 %")
    }





    var input2 = document.getElementById('porcentajeC2');//inicio2
    input2.addEventListener('input', function () {
        if (this.value.length > 2)
            this.value = this.value.slice(0, 2);
    })

    if (input2.value.length === 2) {
        console.log("guardar cambios cambio...." + input2.value)
    } // fin 2


    var input3 = document.getElementById('porcentajeC3');//inicio3
    input3.addEventListener('input', function () {
        if (this.value.length > 2)
            this.value = this.value.slice(0, 2);
    })

    if (input3.value.length === 2) {
        console.log(input.value + ' :_ : ' + input2.value + ':_ :' + input3.value)
    } // fin 3



    if ((parseInt(input.value)) > 100) {
        alert('no puede eceder mas 100 %')
    }

    else {
        if (parseInt(input.value) + parseInt(input2.value) > 100) {
            alert('no puede eceder mas 100 %')
        }
        else {
            if (parseInt(input.value) + parseInt(input2.value) + parseInt(input3.value) > 100) {
                alert('no puede eceder mas 100 %')
            }
        }
    }

}//fin
