import React from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import GetAppIcon from '@material-ui/icons/GetApp';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import { dataMateria } from '../../../home';
import {getReporteHorarios, getReporteLista} from '../../servicios/api';
import {dataReportHorario, dataReportLista} from '../../servicios/api';

import {useStyles} from './dialogos_principal'
import  jsPDF  from  'jspdf'
import 'jspdf-autotable'

export default function ComposedTextField() {


  const informacionPdf = async () =>{//http://localhost:4000/api/personal/consultar/reporte/lista/7/403/251/11
    try {

      console.log("1")
          const ID_MATERIA = dataMateria[0].idMateria;
          const PERIODO = dataMateria[0].idnomenclaturaPeriodo;
          const GRUPO = dataMateria[0].idGrupos;
          
          await Promise.all([getReporteHorarios(PERIODO,ID_MATERIA,GRUPO),getReporteLista(PERIODO,ID_MATERIA,GRUPO)])
          console.log("2")

          await pdfAsistencia()
          console.log("3")

    
    } catch (error) {
      console.log(error)
    }
      }

  var dataAlumnos=[{
    id:'1', control:'14E20301', nombre:'SAMUEL RIVAS GARCIA',curso:'ESCOLARIZADO', opcion:'O',c1:'100', c2:'90',c3:'90', accion:'l' ,v:'4',vv:'2',vvv:'1'},
    {id:'2', control:'14E20302', nombre:'DANY CAMBRANO ARCOZ',curso:'ESCOLARIZADO', opcion:'O',c1:'9', c2:'8',c3:'7.3'},
    {id:'3', control:'14E20303', nombre:'LUIS ALVERTO DE LA CRUZ DIAZ',curso:'ESCOLARIZADO', opcion:'O',c1:'9', c2:'8',c3:'9.7'},
   { id:'4', control:'14E20304', nombre:'JOSE BIRRUTTA CASTROS',curso:'ESCOLARIZADOd', opcion:'O',c1:'9.4', c2:'8.3',c3:'9.7'},
   
   { id:'5', control:'14E20304', nombre:'PEDRO BIRRUTTA CASTROS',curso:'ESCOLARIZADOd', opcion:'O',c1:'9.1', c2:'8',c3:'7'},
   
   { id:'6', control:'14E20304', nombre:'JUAN BIRRUTTA CASTROS',curso:'ESCOLARIZADOd', opcion:'O',c1:'2', c2:'3.4',c3:'9'},
   
   { id:'7', control:'14E20304', nombre:'MANUEL BIRRUTTA CASTROS',curso:'ESCOLARIZADOd', opcion:'O',c1:'4', c2:'8',c3:'9.1'}


];
 
  const pdfAsistencia =()=>{
    const Horas_clases = dataReportHorario[0].semanas;
    const Grupo = dataReportHorario[0].grupo;
    console.log("4")

    console.log(Horas_clases)

    const pdf = new jsPDF('p', 'pt', 'letter')
    pdf.setFontSize(14)
    pdf.text(130, 15, 'INSTITUTO TECNOLOGICO SUPERIOR DE LOS RIOS')
    pdf.line(100, 17, 500, 17) // horizontal line
    pdf.setFontSize(8)
    pdf.text(262,26,"Lista de asistencia");
    pdf.roundedRect(102, 30, 400, 12,3,3)
    pdf.setFontSize(7)
    pdf.text(262,40,Horas_clases);
    
    var columns = [{ title: "Control", dataKey: "numeroControl" },{ title: "Nombre", dataKey: "nombre" },
    { title: "", dataKey: "" },
    { title: "", dataKey: "" },
    { title: "", dataKey: "" },
    { title: "", dataKey: "" },
    { title: "", dataKey: "" },
    { title: "", dataKey: "" },
    { title: "", dataKey: "" },
    { title: "", dataKey: "" },
    { title: "", dataKey: "" },
    { title: "", dataKey: "" },
    { title: "", dataKey: "" },
    { title: "", dataKey: "" },
    { title: "", dataKey: "" },
    { title: "", dataKey: "" },
    { title: "", dataKey: "" },
    { title: "", dataKey: "" },
    { title: "C", dataKey: "modalidad" },
  ];
    
    pdf.autoTable(columns,dataReportLista,
    { margin:{ top: 55 },
    styles: { halign: 'center', cellPadding: 0.5,fontSize:7},
    theme:'grid',
    
  }   
  
    );
    
    pdf.save('mipdf.pdf');
    console.log(dataReportLista)

  }



  const classes = useStyles();
  return (
    <React.Fragment>
    <CssBaseline />
    <Container maxWidth="sm">
    <div className={classes.avatar}>
    <Avatar src="/broken-image.jpg" />
    </div>
      <Typography variant="button" display="block" gutterBottom>
        Nombre: {dataMateria[0].nameDocente}
      </Typography>
      <Typography variant="button" display="block" gutterBottom>
        Clave: {dataMateria[0].clavePersonal}
      </Typography>
      <Typography variant="button" display="block" gutterBottom>
        Divicion: {dataMateria[0].nombreCorto}
      </Typography>
      <div className={classes.pdfss}>
      <Button 
       onClick={informacionPdf}
        variant="contained"
        color="default"
        startIcon={<GetAppIcon />}
      >
        Descargar lista de asistencia
      </Button>
      </div>
    </Container>
    
  </React.Fragment>
   
  
      

   
      
    
      
     
      
    
  );
}
