import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';

const useStyles = makeStyles((theme) => ({
	root: {
		'& > *': {
			margin: theme.spacing(1),
			width: '25ch'
		}
	}
}));

function ValidarCriterios() {
	const classes = useStyles();

	const CRITERIO_C1 = React.useRef(0)
	const CRITERIO_C2 = React.useRef(0)
	const CRITERIO_C3 = React.useRef(0)
	const CRITERIO_C4 = React.useRef(0)
	var LIMITE_CRITERIOS = 100;



	function confiCriterio1() {
		let C1_REFERENCIA = CRITERIO_C1.current.value;
		if(LIMITE_CRITERIOS > 0){
			if(C1_REFERENCIA.length === 2){
				LIMITE_CRITERIOS =(LIMITE_CRITERIOS-C1_REFERENCIA)
			}
			CRITERIO_C1.current.value= Math.max(0, Math.min(parseInt(CRITERIO_C1.current.value || 0),LIMITE_CRITERIOS)).toString().slice(0, 2)
		    console.log(LIMITE_CRITERIOS)
		}

		
	}

	function confiCriterio2(params) {
		let C2_REFERENCIA = CRITERIO_C2.current.value;

		if(LIMITE_CRITERIOS > 0){
			if(C2_REFERENCIA.length >= 2){
				LIMITE_CRITERIOS =(LIMITE_CRITERIOS-C2_REFERENCIA)
			}
		}

		CRITERIO_C2.current.value=Math.max(0, Math.min(parseInt(CRITERIO_C2.current.value || 0),LIMITE_CRITERIOS)).toString().slice(0, 2)
		console.log(LIMITE_CRITERIOS)
		
	}
	function confiCriterio3(params) {
		let C3_REFERENCIA = CRITERIO_C3.current.value;

		if(LIMITE_CRITERIOS > 0){
			if(C3_REFERENCIA.length >= 2){
				LIMITE_CRITERIOS =(LIMITE_CRITERIOS-C3_REFERENCIA)
			}
		}

		CRITERIO_C3.current.value=Math.max(0, Math.min(parseInt(CRITERIO_C3.current.value || 0),LIMITE_CRITERIOS)).toString().slice(0, 2)
		console.log(LIMITE_CRITERIOS)
		
	}
	function confiCriterio4(params) {
		let C4_REFERENCIA = CRITERIO_C4.current.value;

		if(LIMITE_CRITERIOS > 0){
			if(C4_REFERENCIA.length >= 2){
				LIMITE_CRITERIOS =(LIMITE_CRITERIOS-C4_REFERENCIA)
			}
		}

		CRITERIO_C4.current.value=Math.max(0, Math.min(parseInt(CRITERIO_C4.current.value || 0),LIMITE_CRITERIOS)).toString().slice(0, 2)
		console.log(LIMITE_CRITERIOS)
	}




	return (
		<form className={classes.root} noValidate autoComplete="off">
			<TextField
			inputRef={CRITERIO_C1}
			onChange={confiCriterio1}
				type="number"
				name="c1"
				maxLength="2"
				id="filled-basic1"
				label="C1"
				variant="filled"
				onInput={(e) => {
					e.target.value = CRITERIO_C1.current.value;
				}}
				min={0}
				
			/>
			<TextField
				type="number"
				inputRef={CRITERIO_C2}
			onChange={confiCriterio2}
				name="c2"
				id="filled-basic2"
				label="C2"
				variant="filled"
				onInput={(e) => {
					e.target.value = CRITERIO_C2.current.value;
				}}
				min={0}
			/>
			<TextField
				type="number"
				inputRef={CRITERIO_C3}
				onChange={confiCriterio3}
				name="c3"
				id="filled-basic3"
				label="C3"
				variant="filled"
				onInput={(e) => {
					e.target.value = CRITERIO_C3.current.value;
				}}
				min={0}
			/>
			<TextField
				type="number"
				inputRef={CRITERIO_C4}
				onChange={confiCriterio4}
				name="c4"
				id="filled-basic4"
				label="C4"
				variant="filled"
				onInput={(e) => {
					e.target.value = CRITERIO_C4.current.value;
				}}
				min={0}
			/>
		</form>
	);
}

export default React.memo(ValidarCriterios);
