import React from 'react';
import Button from '@material-ui/core/Button';


export const Buttons  = ({texto="btn" , color="secondary", onGenerar}) => {
  const [activo, setActio] = React.useState(false)

            const ejecu = async (e)=>{
                e.preventDefault()
                setActio(true)
               await onGenerar()
                setActio(false)

            }

    return(
        <Button disabled={activo} variant="contained" onClick={ejecu} color={color}>
            {texto}
       </Button>
    )
}
