import React from 'react';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import { datalista,datalistaAlumnos, dataCriterios,dataPeriodo, getAlumnos, getAdmiFechas } from '../../servicios/api';

import { useStyles } from './dialogos_calificacion';


export const SelectTemas = (data) => {
    console.log(datalista)
    console.log('selctMterias')

  const [unidad, setUnidad] = React.useState('');
  const [datalista1, setDatalista] = React.useState(datalista);


    const estilos = useStyles();


    const _obtenerTema = async (tem) => {//inico
        console.log('hook obtenerTema')
    
        data.setOpen(true)
        let numTemas = tem.target.value;
        setUnidad(numTemas);
        await getAlumnos(data.MATERIA_ID, numTemas);//LISTA DE ALUMNOS  Pendiene mandar unidad que es el tema #
        await data.setcalificaciones({ datalistaAlumnos: datalistaAlumnos });
       
        await data.updates(data.MATERIA_ID,numTemas)
        console.log(dataCriterios)
        data.setOpen(false)
    
        data.unidadCalificacion = dataCriterios[0].numUnidad;//public unidad del criterio seleccinado
        data.id_criterios = dataCriterios[0].idcat_Unidad;//public id_criterios del criterio seleccinado
      }//fin


    //const _obtenerTemam = (e) => {
      //  e.preventDefault();
        //data.lisTemas(e)
   // }

    return (
        <div>
            <FormControl variant="outlined" className={estilos.formControl}>
                <InputLabel id="demo-simple-select-outlined-labe">Unidad</InputLabel>
                <Select
                    labelId="demo-simple-select-outlined-labe"
                    id="unidad"
                    label="Unidad"
                    value={unidad}
                    onChange={_obtenerTema}>
                    {datalista1.map((tem, i) => (<MenuItem key={i} value={tem.numUnidad}>{tem.tema}</MenuItem>))}
                </Select>
            </FormControl>
        </div>
    );
}
