            cont++
        }
console.log('fin de for aprobacion5')
    }//fin

    var sduper = [
        { nm: 1, numeroControl: "20E20004", nombre: "SARAHI MARLENE MALDONADO JUAREZ", modalidad: "" },
        { nm: 2, numeroControl: "20E20002", nombre: "ERICK MOISES HERRERA  DE LA CRUZ", modalidad: "" },
        { nm: 3, numeroControl: "20E20011", nombre: "JORGE ARCOS VAZQUEZ", modalidad: "" },
        { nm: 4, numeroControl: "20E20006", nombre: "RODRIGO MORALES PEREZ", modalidad: "" },
        { nm: 5, numeroControl: "20E20010", nombre: "CRISTOPHER LUIS LÓPEZ ARRAIZ", modalidad: "" }];


    const pdf = (nomMateria, docente_actual) => {
        const Horas_clases = dataReportHorario[0].semanas;
