import React from 'react';
import MaterialTable from 'material-table';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import SaveIcon from '@material-ui/icons/Save';

//importdos
import {data} from './clase_principal_calificaciones';
import './calificaciones.css';
//variables
import { tema1X, tema2X, tema3X, porcentajeActual, dataAlumnos } from './clase_principal_calificaciones';


export default function MaterialTableDemo() {
    
  const manejador = (cc1,cc2,cc3) => {//creador de table

    console.log("imprimiendo....." + tema1X)
    console.log("datos new....." + cc1+cc2+cc3)

    
   
  };

  
  const [state, setState] = React.useState({
    columns: [
      { title: 'Nª', field: 'id' },
      { title: 'Control', field: 'control' },
      { title: 'Birth Year', field: 'birthYear', type: 'numeric' },
      {
        title: 'Birth Place',
        field: 'birthCity',
        lookup: { 34: 'İstanbul', 63: 'Şanlıurfa' },
      },
    ],
    data: [
      { name: 'Mehmet', surname: 'Baran', birthYear: 1987, birthCity: 63 },
      {
        name: 'Zerya Betül',
        surname: 'Baran',
        birthYear: 2017,
        birthCity: 34,
      },
    ],
  });
const [alumnos,setAlumnos] =React.useState({ columns: [
    { title: 'Nª', field: 'id', editable:'never'  },
    { title: 'Control', field: 'control', editable:'never' },
    { title: 'Nombre', field: 'nombre', editable:'never' },
    {
      title: 'Curso',
      field: 'curso',
      editable:'never',
     // lookup: { 34: 'İstanbul', 63: 'Şanlıurfa' },
    },
    { title: 'Opcion', field: 'opcion', editable:'never' },
    { title: 'C1', field: 'c1',  },
    { title: 'C2', field: 'c2', },
    { title: 'C3', field: 'c3',  },
    { title: '#', field: '#', editable:'never'  },
    { title: <input className="inputTemas" ></input>, field: 'v',with:2, editable:'never'  },

    { title: <input className="inputTemas" ></input>, field: 'vv', editable:'never'  },

    { title: <input className="inputTemas" ></input>, field: 'vvv', editable:'never'  },
    { title: 'Total', field: '#', editable:'never'  },




  ],
    data})
  return (
    <div>
    <Grid container spacing={3}>
      <Grid item xs={12}>
        <Paper > INSTITUTO TECNOLOGICO SUPERIOR DE LOS RIOS </Paper>
      </Grid>
      <Grid item xs={12} sm={6}>
        <Paper >Cnponente1</Paper>
      </Grid>
      <Grid item xs={12} sm={6}>
        <Paper >libre</Paper>
      </Grid>
      <Grid item xs={12} sm={12}>
        {/*   <Paper > <Paper className={classes.root}>
    <TableContainer className={classes.container}>*/}
        <Paper >
        <MaterialTable
      title="Editable Example"
      columns={alumnos.columns}
      data = {alumnos.data}

      actions={[
        {
          icon: 'save',
          tooltip: 'Save User',
          onClick: (event, rowData) => alert("You saved " + rowData.control +" c1=" +rowData.c1)
        }
      ]}

      editable={ 
        {
       // filsa: rowData => rowData.id === '1',
        onRowUpdate: (newData, oldData) =>
          new Promise(resolve => {
            setTimeout(() => {
              resolve();

              if (oldData) {
                console.log(resolve.c1)


                setAlumnos(prevState => {
                  const data = [...prevState.data];//obtenr data
                  console.log(newData)//estado fila modificado
                  data[data.indexOf(oldData)] = newData;
                  console.log(data)
                  manejador(newData.c1, newData.c2, newData.c3)
                  return { ...prevState, data };
                });



              }
            }, 600);
          }),
      
      }
    }

      options={{
        headerStyle: {
          backgroundColor: '#01579b',
          color: '#FFF'
        }
      }}
    />
        </Paper>
      </Grid>
      <Grid item xs={12} sm={6}>
        <Paper >*O: Ordinario   *1:Primera oportunidad   *NA: No Alcanza</Paper>
        <Paper >*R: Repeticion   *2:Segunda oportunidad</Paper>
        <Paper >*E: Especial</Paper>
      </Grid>
      <Grid item xs={12}>
        <Button
          variant="contained"
          color="primary"
          size="small"
          onClick={manejador}
          startIcon={<SaveIcon />}
        >
          Descargar Reporte de Calificaciones
    </Button>
      </Grid>
    </Grid>

  </div>
  
  );
}
