import React,{useState, useEffect} from 'react';
import {connect} from 'react-redux'
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import Docente_principal from './contenedores/docente_principal/vista_principal';
import Docente_calendario from './contenedores/docente_calendario/docente_calendario';
import Clase_principal_calificaciones from './contenedores/docente_calificaciones/clase_principal_calificaciones'
import Grid from '@material-ui/core/Grid';

function TabPanel(props) {


  const { children, value, index, ...other } = props;

  return (
    <Typography
      component="div"
      role="tabpanel"
      hidden={value !== index}
      id={`wrapped-tabpanel-${index}`}
      aria-labelledby={`wrapped-tab-${index}`}
      {...other}
    >
      {value === index && <Box p={3}>{children}</Box>}
    </Typography>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
};

function a11yProps(index) {
  return {
    id: `wrapped-tab-${index}`,
    'aria-controls': `wrapped-tabpanel-${index}`,
  };
}

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
    backgroundColor: theme.palette.background.paper,
  },
 
}));

 const  TabsWrappedLabel =(updateMarerias) => {

  useEffect(() => {
    // Actualiza el título del documento usando la API del navegador
   console.log("estoy actualiando mareias...")
  });

  const classes = useStyles();
  const [value, setValue] = React.useState('one');

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <div className={classes.root}>
      <AppBar position="static">
        <Tabs value={value} onChange={handleChange} aria-label="wrapped label tabs example">
          <Tab
            value="one"
            label="Principal"
            wrapped
            {...a11yProps('one')}
          />
          <Tab value="two" label="Calendario" {...a11yProps('two')} />
          <Tab value="three" label="Calificaciones" {...a11yProps('three')} />
        </Tabs>
      </AppBar>
      <TabPanel value={value} index="one">
      <Grid item xs={12}>
     <Docente_principal />
          
        </Grid>
      </TabPanel>
      <TabPanel value={value} index="two">
        <Docente_calendario/>
      </TabPanel>
      <TabPanel value={value} index="three">
      <Clase_principal_calificaciones/>

      </TabPanel>
    </div>
  );
}

const mapStateToProps = state => ({
  generalAlumnos: state.alumnosState,
  materiasDocentes: state.materias,
  porcentaje: state.porcentaje
})

const mapDispatchToProps = dispatch => ({

  updateMarerias(materiasDoncente){
    dispatch({
      type:"updateMarerias",
      materiasDoncente
    })
  }
 
  
})
export default connect(mapStateToProps, mapDispatchToProps)(TabsWrappedLabel)
