import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';

const useStyles = makeStyles((theme) => ({
	root: {
		'& > *': {
			margin: theme.spacing(1),
			width: '25ch'
		}
	}
}));

function ValidarCriterios() {
	const classes = useStyles();

	const CRITERIO_C1 = React.useRef(0)
	const CRITERIO_C2 = React.useRef(0)
	const CRITERIO_C3 = React.useRef(0)
	const CRITERIO_C4 = React.useRef(0)


	const _aprobar = (evt) => {
		let max = 100;
		console.log(CRITERIO_C1.input.value);
	};

	return (
		<form className={classes.root} noValidate autoComplete="off">
			<TextField
			ref={CRITERIO_C1}
			onChange={_aprobar}
				type="number"
				name="c1"
				maxLength="2"
				id="filled-basic1"
				label="C1"
				variant="filled"
				onInput={(e) => {
					e.target.value = Math.max(0, parseInt(e.target.value)).toString().slice(0, 2);
				}}
				min={0}
				
			/>
			<TextField
				type="number"
				name="c2"
				id="filled-basic2"
				label="C2"
				variant="filled"
				onInput={(e) => {
					e.target.value = Math.max(0, parseInt(e.target.value)).toString().slice(0, 2);
				}}
				min={0}
			/>
			<TextField
				type="number"
				name="c3"
				id="filled-basic3"
				label="C3"
				variant="filled"
				onInput={(e) => {
					e.target.value = Math.max(0, parseInt(e.target.value)).toString().slice(0, 2);
				}}
				min={0}
			/>
			<TextField
				type="number"
				name="c4"
				id="filled-basic4"
				label="C4"
				variant="filled"
				onInput={(e) => {
					e.target.value = Math.max(0, parseInt(e.target.value)).toString().slice(0, 2);
				}}
				min={0}
			/>
		</form>
	);
}

export default React.memo(ValidarCriterios);
