import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';

const useStyles = makeStyles((theme) => ({
	root: {
		'& > *': {
			margin: theme.spacing(1),
			width: '25ch'
		}
	}
}));

function ValidarCriterios() {
	const classes = useStyles();

	const CRITERIO_C1 = React.useRef(0)
	const CRITERIO_C2 = React.useRef(0)
	const CRITERIO_C3 = React.useRef(0)
	const CRITERIO_C4 = React.useRef(0)
	var LIMITE_CRITERIOS = 100;


	const _aprobar = (evt) => {
		let LIMITE = 100;
		let C1_REFERENCIA = CRITERIO_C1.current.value;
		let C2_REFERENCIA = CRITERIO_C2.current.value;
		let C3_REFERENCIA = CRITERIO_C3.current.value;
		let C4_REFERENCIA = CRITERIO_C4.current.value;

		console.log(C1_REFERENCIA.length);
		console.log(C2_REFERENCIA);
		console.log(C3_REFERENCIA);
		console.log(C4_REFERENCIA);
		
		
		if(LIMITE_CRITERIOS > 0){
			if(C1_REFERENCIA.length >= 2){
				LIMITE_CRITERIOS =(LIMITE_CRITERIOS-C1_REFERENCIA)
			}
		}

		CRITERIO_C1.current.value= Math.max(0, Math.min(parseInt(CRITERIO_C1.current.value || 0),LIMITE_CRITERIOS)).toString().slice(0, 3)
		CRITERIO_C2.current.value=Math.max(0, Math.min(parseInt(CRITERIO_C2.current.value || 0),LIMITE_CRITERIOS)).toString().slice(0, 3)
		CRITERIO_C3.current.value=Math.max(0, Math.min(parseInt(CRITERIO_C3.current.value || 0),LIMITE_CRITERIOS)).toString().slice(0, 3)
		CRITERIO_C4.current.value=Math.max(0, Math.min(parseInt(CRITERIO_C4.current.value || 0),LIMITE_CRITERIOS)).toString().slice(0, 3)
		console.log(LIMITE_CRITERIOS)//100

	};

	return (
		<form className={classes.root} noValidate autoComplete="off">
			<TextField
			inputRef={CRITERIO_C1}
			onChange={_aprobar}
				type="number"
				name="c1"
				maxLength="2"
				id="filled-basic1"
				label="C1"
				variant="filled"
				onInput={(e) => {
					e.target.value = CRITERIO_C1.current.value;
				}}
				min={0}
				
			/>
			<TextField
				type="number"
				inputRef={CRITERIO_C2}
			onChange={_aprobar}
				name="c2"
				id="filled-basic2"
				label="C2"
				variant="filled"
				onInput={(e) => {
					e.target.value = CRITERIO_C2.current.value;
				}}
				min={0}
			/>
			<TextField
				type="number"
				inputRef={CRITERIO_C3}
				onChange={_aprobar}
				name="c3"
				id="filled-basic3"
				label="C3"
				variant="filled"
				onInput={(e) => {
					e.target.value = CRITERIO_C3.current.value;
				}}
				min={0}
			/>
			<TextField
				type="number"
				inputRef={CRITERIO_C4}
				onChange={_aprobar}
				name="c4"
				id="filled-basic4"
				label="C4"
				variant="filled"
				onInput={(e) => {
					e.target.value = CRITERIO_C4.current.value;
				}}
				min={0}
			/>
		</form>
	);
}

export default React.memo(ValidarCriterios);
