import React from 'react';
import MaterialTable from 'material-table';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper'; 
import SaveIcon from '@material-ui/icons/Save';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import { makeStyles } from '@material-ui/core/styles';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import InputAdornment from '@material-ui/core/InputAdornment';
//importdos
import {data} from './clase_principal_calificaciones';
import './calificaciones.css';
//variables
import { tema1X, tema2X, tema3X, porcentajeActual, dataAlumnos } from './clase_principal_calificaciones';



const useStyles = makeStyles(theme => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 260,
  },
  selectEmpty: {
    marginTop: theme.spacing(-1),
  },
  papermaterias: {
    padding: theme.spacing(-1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  paperperiodos: {
    padding: theme.spacing(1),
    textAlign: 'left',
    color: theme.palette.text.secondary,
  },
}));

export default function MaterialTableDemo() {
  const estilos = useStyles();
    //actualizacion del nuevo estado
    const [age, setAge] = React.useState('');
    const handleChange = event => {
      setAge(event.target.value);
    };
    //
  const manejador = (cc1,cc2,cc3) => {//creador de table

    console.log("imprimiendo....." + tema1X)
    console.log("datos new....." + cc1+cc2+cc3)

    
   
  };


const [alumnos,setAlumnos] =React.useState({ columns: [
    { title: 'Nª', field: 'id', editable:'never'  },
    { title: 'Control', field: 'control', editable:'never' },
    { title: 'Nombre', field: 'nombre', editable:'never' },
    {
      title: 'Curso',
      field: 'curso',
      editable:'never',
     // lookup: { 34: 'İstanbul', 63: 'Şanlıurfa' },
    },
    { title: 'Opcion', field: 'opcion', editable:'never' },
    { title: 'C1', field: 'c1',  },
    { title: 'C2', field: 'c2', },
    { title: 'C3', field: 'c3',  },
    { title: '#', field: '#', editable:'never'  },
    { title: 
      <FormControl  variant="outlined">
      <OutlinedInput
        id="outlined-adornment-weight"
        
        placeholder={"'"+tema1X+"'"}
        endAdornment={<InputAdornment position="end">%</InputAdornment>}
        aria-describedby="outlined-weight-helper-text"
        
        labelWidth={0}
      />
    
    </FormControl>, field: 'v',with:2, editable:'never'  },

    { title: <input className="inputTemas" placeholder={tema2X}></input>, field: 'vv', editable:'never'  },

    { title: <input className="inputTemas" placeholder={tema3X} ></input>, field: 'vvv', editable:'never'  },
    { title: 'Total', field: '#', editable:'never'  },




  ],
    data})
  return (
    <div>
    <Grid container spacing={3}>
      <Grid item xs={12}>
        <h1 > INSTITUTO TECNOLOGICO SUPERIOR DE LOS RIOS </h1>
      </Grid>
      <Grid item xs={6} sm={3}>
          <Paper elevation={0} className={estilos.paperperiodos}>PERIODO:</Paper>
        </Grid>
        <Grid item xs={6} sm={3}>
          <Paper className={estilos.paperperiodos}>CIERRE DE ACTA:</Paper>
        </Grid>
        <Grid item xs={12} sm={6}>
          <Paper >xs=12 sm=33</Paper>
        </Grid>

        <Grid item xs={6} sm={3}>
          <Paper className={estilos.papermaterias}>
          <FormControl variant="outlined" className={estilos.formControl}>
        <InputLabel id="demo-simple-select-outlined-label">Materia</InputLabel>
        <Select
          labelId="demo-simple-select-outlined-label"
          id="demo-simple-select-outlined"
          value={age}
          onChange={handleChange}
          label="Materia"
        >
          {
            dataAlumnos.map((materias,i)=>( <MenuItem value={materias.id}>{materias.nombre}</MenuItem>))         
           }
          
        </Select>
      </FormControl>
            </Paper>
        </Grid>

        <Grid item xs={6} sm={3}>
          <Paper className={estilos.papermaterias}>
          <FormControl variant="outlined" className={estilos.formControl}>
        <InputLabel id="demo-simple-select-outlined-label">Materia</InputLabel>
        <Select
          labelId="demo-simple-select-outlined-label"
          id="demo-simple-select-outlined2"
          value={age}
          onChange={handleChange}
          label="Materia"
        >
          <MenuItem value="">
            <em>None</em>
          </MenuItem>
          <MenuItem value={10}>Matematicas discretas</MenuItem>
          <MenuItem value={20}>Twenty</MenuItem>
          <MenuItem value={30}>Thirty</MenuItem>
        </Select>
      </FormControl>

          </Paper>
        </Grid>

      <Grid item xs={12} sm={6}>
       
      </Grid>
      

      <Grid item xs={12} sm={12}>
        {/*   <Paper > <Paper className={classes.root}>
    <TableContainer className={classes.container}>*/}
        <Paper elevation={3} >
        <MaterialTable
      title="Captura de calificaciones"
      columns={alumnos.columns}//columnas
      data = {alumnos.data}//filas

      actions={[
        {
          icon: 'save',
          tooltip: 'Save User',
          onClick: (event, rowData) => alert("You saved " + rowData.control +" c1=" +rowData.c1)
        }
      ]}

      editable={ 
        {
       // filsa: rowData => rowData.id === '1',
        onRowUpdate: (newData, oldData) =>
          new Promise(resolve => {
            setTimeout(() => {
              resolve();

              if (oldData) {
                console.log(resolve.c1)
//actualizacion del estado
                setAlumnos(prevState => {
                  const data = [...prevState.data];//obtenr data
                  console.log(newData)//estado fila modificado
                  data[data.indexOf(oldData)] = newData;
                  manejador(newData.c1, newData.c2, newData.c3)
                  return { ...prevState, data };
                });



              }
            }, 600);
          }),
      
      }
    }

      options={{
        headerStyle: {
          backgroundColor: '#01579b',
          color: '#FFF'
        }
      }}
    />
        </Paper>
      </Grid>
      <Grid item xs={12} sm={6}>
        <Paper >*O: Ordinario   *1:Primera oportunidad   *NA: No Alcanza</Paper>
        <Paper >*R: Repeticion   *2:Segunda oportunidad</Paper>
        <Paper >*E: Especial</Paper>
      </Grid>
      <Grid item xs={12}>
        <Button
          variant="contained"
          color="primary"
          size="small"
          onClick={manejador}
          startIcon={<SaveIcon />}
        >
          Descargar Reporte de Calificaciones
    </Button>
      </Grid>
    </Grid>

  </div>
  
  );
}
