import React from 'react';
import Backdrop from '@material-ui/core/Backdrop';
import CircularProgress from '@material-ui/core/CircularProgress';
import Button from '@material-ui/core/Button';
import swal from 'sweetalert';
import { crearRegistrosfechas,updateRegistrosfechas, EXISTNCIA_ACTA, getPeriodo } from '../servicios/api';
import { useStyles } from './styles';



export const Btn_evaluar = (fechas) => {
console.log(fechas)
    const classes = useStyles();
    const [loading, setLoading] = React.useState(false);

    const enviar = async () => {
      //  if (!EXISTNCIA_ACTA) {
            if ((fechas.segunda > fechas.primera) && (fechas.tercera > fechas.segunda) && (fechas.final > fechas.tercera)) {
               
                setLoading(true)
                if(fechas.isDisable){
                    console.log('creando fechas')
                    await crearRegistrosfechas(fechas);
                }else{
                    console.log('actualizando fechas')
                    await updateRegistrosfechas(fechas)
                }
                //await getPeriodo();
                fechas.setRecargar(true)
                setLoading(false)
            } else {
                swal("Verifique la cronología de fechas, la segunda entrega no puede ser antes que la primera", {
                    button: false,
                  });
            }
       /* } else {
            swal("Disponible hasta el siguiente periodo", {
                button: false,
              });
            
        }*/
    }

    return (
        <>
            
            <p>{loading}</p>
            <Backdrop className={classes.backdrop} open={loading} >
                <CircularProgress color="inherit" />
            </Backdrop>
            <Button variant="outlined" color="primary" onClick={enviar} > Guardar</Button>
        </>
    );
}

export default { Btn_evaluar }
