import React from 'react';
import { Buttons } from '../button/button';
import moment from 'moment';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import Itsr from '../../../../img/Logo-Tec.png';
import { dataMateria } from '../../../../../home';
import { getReporteHorarios, getReporteParcial, dataReportHorario, dataPeriodo, dataReporteParciales } from '../../../../servicios/api';

var miArray = [];
export const ReportParciales = (data) => {
    console.log(data)

    const infPdf = async () => {
        console.log("imprimiendo.... .")
        try {
            const DOCENTE_ACTUAL = dataMateria[0].nameDocente;
            for (let index = 0; index < dataMateria.length; index++) {
                const ID_MATERIA = dataMateria[index].idMateria;
                const PERIODO = dataMateria[index].idnomenclaturaPeriodo;
                const GRUPO = dataMateria[index].idGrupos;
                const Materia = dataMateria[index].nombre;
                //periodo materia personal grupo
                await Promise.all([getReporteHorarios(PERIODO, ID_MATERIA, GRUPO), getReporteParcial(ID_MATERIA, GRUPO)])
                await pdf(Materia, DOCENTE_ACTUAL)

                miArray[index]  = { id: 1, nombre: "RAFAEL PEREZ", edad: 23, activo: true };
                
            }
            console.log(miArray)
        } catch (error) {
            console.log(error)
        }
    }

    var sduper = [
        { nm: 1, numeroControl: "20E20004", nombre: "SARAHI MARLENE MALDONADO JUAREZ", modalidad: "" },
        { nm: 2, numeroControl: "20E20002", nombre: "ERICK MOISES HERRERA  DE LA CRUZ", modalidad: "" },
        { nm: 3, numeroControl: "20E20011", nombre: "JORGE ARCOS VAZQUEZ", modalidad: "" },
        { nm: 4, numeroControl: "20E20006", nombre: "RODRIGO MORALES PEREZ", modalidad: "" },
        { nm: 5, numeroControl: "20E20010", nombre: "CRISTOPHER LUIS LÓPEZ ARRAIZ", modalidad: "" }];


    const pdf = (nomMateria, docente_actual) => {
        const Horas_clases = dataReportHorario[0].semanas;
        const Grupo = dataReportHorario[0].grupo;
        const Semestre = dataReportHorario[0].semestre;

        var img = new Image();
        img.src = Itsr;

        const pdf = new jsPDF('p', 'pt', 'letter')
        //body

        var columns = [
            { title: "Nº", dataKey: "nm" },
            { title: "CONTROL", dataKey: "numeroControl" },
            { title: "ESTUDIANTES", dataKey: "nombreAspirante", },
            { title: "O1", dataKey: "tema1" },
            { title: "02", dataKey: "tema2" },
            { title: "03", dataKey: "tema3" },
            { title: "04", dataKey: "tema4" },
            { title: "05", dataKey: "tema5" },
            { title: "06", dataKey: "tema6" },
            { title: "07", dataKey: "tema7" },
            { title: "08", dataKey: "tema8" },
            { title: "09", dataKey: "tema9" },
            { title: "10", dataKey: "tema10" },
            { title: "CAL", dataKey: (9 * 9) },
            { title: "TC", dataKey: "modalidad" }
        ];

        pdf.autoTable(columns, dataReporteParciales,
            {
                margin: { top: 75 },
                styles: { halign: 'center', cellPadding: 0.5, fontSize: 7 },
                theme: 'grid',

            }
        );


        //piede paginas
        const pageCount = pdf.internal.getNumberOfPages();
        for (var i = 1; i <= pageCount; i++) {
            pdf.setPage(i);


            pdf.setFontSize(14)//encabezado
            pdf.text(130, 15, 'INSTITUTO TECNOLÓGICO SUPERIOR DE LOS RÍOS')
            pdf.line(100, 17, 500, 17) // horizontal line
            pdf.setFontSize(8)
            pdf.text(262, 26, "PRELISTA CON PARCIALES");
            pdf.roundedRect(102, 45, 400, 12, 3, 3)
            pdf.setFontSize(8)
            pdf.text(100, 40, `MATERIA: ${nomMateria}`);
            pdf.setFontSize(8)
            pdf.text(500, 31, `PERIODO: ${dataPeriodo[0].rango}`);
            pdf.setFontSize(8)
            pdf.text(500, 40, `FECHA: ${moment().format('L')}`);
            pdf.setFontSize(7)
            pdf.text(122, 54, Horas_clases);
            pdf.setFontSize(8)
            pdf.text(500, 66, `GRUPO: ${Semestre} ${Grupo}`);
            pdf.setFontSize(8)
            pdf.text(100, 66, `DOCENTE: ${docente_actual}`);
            pdf.addImage(img, 'PNG', 13, 10, 63, 63)


            pdf.setFontSize(7)//pie de pagina
            pdf.text(20, 760, "C (Curso, R:Repetición, E:Especial)");
            pdf.setLineWidth(1.6)
            pdf.line(390, 760, 500, 760)
            pdf.setFontSize(7)
            pdf.text(413, 767, "FIRMA DOCENTE");
            pdf.text(20, 770, 'Pagina' + String(i) + ' de ' + String(pageCount), null, null, null, null, "right");
        }
        pdf.save(`PARCIALES ${nomMateria}.pdf`);

    }

    return (
        <div>
            <Buttons texto="Calificaciones Parciales" onGenerar={infPdf} color="primary" />
        </div>
    );
}
