import React from 'react';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import {getAlumnos, datalistaAlumnos, MATERIA_ID, dataCriterios} from '../../servicios/api'


import { useStyles } from './dialogos_calificacion';

export const SelecMaterias = (data) => {
    const estilos = useStyles();

    const _buscarTema = async (tem) => {//inico
        data.setOpen(true)
        let numTemas = tem.target.value;
        data.setUnidad(numTemas);
        await getAlumnos(MATERIA_ID, numTemas);//LISTA DE ALUMNOS  Pendiene mandar unidad que es el tema #
        await data.setcalificaciones({ datalistaAlumnos: datalistaAlumnos });
        await data.updates(MATERIA_ID,numTemas)
        console.log(dataCriterios)
        data.setOpen(false)
    
        data.unidadCalificacion = dataCriterios[0].numUnidad;//public unidad del criterio seleccinado
        data.id_criterios = dataCriterios[0].idcat_Unidad;//public id_criterios del criterio seleccinado
      }//fin



   // const _buscarTema = (e) => {
     //   e.preventDefault()
       // data.lisMateria(e)
    //}

    return (
        <div>
            <FormControl variant="outlined" className={estilos.formControl}>
                <InputLabel id="InputLabel">Materia</InputLabel>
                <Select
                    labelId="demo-simple-select-outlined"
                    id="materia"
                    onChange={_buscarTema}
                    label="Materia"
                    value={data.materia}>
                    {data.dataMateria.map((materias) => (<MenuItem key={materias.nm} value={materias.idMateria} >{materias.nombre + ' (' + materias.semestre + "/" + materias.nomenclatura + ") " + materias.nombreCorto}</MenuItem>))}
                </Select>
            </FormControl>
        </div>
    );
}
